import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import { useForm } from 'react-hook-form';
import Commonservice from '../Commonservices';
import Papa from 'papaparse'; // CSV Parsing library

interface ItemData {
  id: number;
  name: string;
  category: string;
  iddsi_level: string;
}

interface ItemsForm {
  name: string;
  category: string;
  iddsi_level: string;
}

const ReadItems: React.FC = () => {
  const { register, handleSubmit, setValue, formState: { errors } } = useForm<ItemsForm>();
  const [itemsData, setItemsData] = useState<ItemData[]>([]); // State to store dynamic data
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentItem, setCurrentItem] = useState<ItemData | null>(null);
  const [showCsvModal, setShowCsvModal] = useState(false); // Modal for CSV Upload

  // Fetch data dynamically when component mounts
  useEffect(() => {
    fetchItemsData(); // Fetch data from the API
  }, []);

  const fetchItemsData = async () => {
    try {
      const response = await Commonservice.getItems(); // Fetch data from the API
      
      // Assuming the response contains data in a property called `data`
      const items = response.data || response;

      // Map and format the response to match your state structure
      const formattedItems = items.map((item: any) => ({
        id: item.id,
        name: item.name, // Adjust field names based on the API response
        category: item.category,
        iddsi_level: item.iddsi_level, // Ensure iddsi_level is correctly mapped
      }));

      setItemsData(formattedItems); // Set the formatted data in state
    } catch (error) {
      console.error('Error fetching items data:', error);
    }
  };

  const handleShow = () => {
    setShowModal(true);
    setEditMode(false);
  };

  const handleClose = () => {
    setShowModal(false);
    setCurrentItem(null);
  };


   // CSV Upload Handler
   const handleCsvUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      Papa.parse(file, {
        complete: function (results: any) {
          const data = results.data;
          const parsedItems: ItemData[] = data.slice(1).map((row: any, index: number) => ({
            id: itemsData.length + index + 1, // Create unique ID
            name: row[0], // Assuming first column is `name`
            category: row[1], // Assuming second column is `category`
            iddsi_level: row[2], // Assuming third column is `iddsi_level`
          }));

          setItemsData([...itemsData, ...parsedItems]);
        },
        header: false,
      });
    }
    handleClose();
  };

  const handleShowCsvModal = () => {
    setShowCsvModal(true);
  };



  const onSubmit = (data: ItemsForm) => {
    if (editMode && currentItem) {
      // Logic for updating the item
      console.log('Updating item:', currentItem.id, data);
      alert('Item updated successfully!');
      
      // Update the state with the updated item
      const updatedItems = itemsData.map(item => 
        item.id === currentItem.id ? { ...item, name: data.name, category: data.category, iddsi_level: data.iddsi_level } : item
      );
      setItemsData(updatedItems);
    } else {
      // Logic for creating a new item
      Commonservice.setItems(data.name, data.category, data.iddsi_level)
        .then(() => {
          alert('Item inserted successfully!');
          
          // Assuming that the backend returns the newly created item, you should ideally fetch it, but for now:
          const newItem: ItemData = {
            id: itemsData.length + 1, // Dummy id, replace this with the id from backend if available
            name: data.name,
            category: data.category,
            iddsi_level: data.iddsi_level,
          };

          setItemsData([...itemsData, newItem]);
        })
        .catch(error => {
          console.error('Error during item insertion:', error);
        });
    }
    handleClose();
  };

  // Edit item handler
  const handleEdit = (item: ItemData) => {
    setEditMode(true);
    setCurrentItem(item);
    setShowModal(true);
    setValue('name', item.name);
    setValue('category', item.category); // Populate category
    setValue('iddsi_level', item.iddsi_level); // Populate IDDSI Level
  };

  // Delete item handler
  const handleDelete = (id: number) => {
    const filteredItems = itemsData.filter(item => item.id !== id);
    setItemsData(filteredItems);
    console.log(`Item with ID ${id} deleted.`);
  };

  // Define columns for DataTable
  const columns = [
    {
      name: 'ID',
      selector: (row: ItemData) => row.id,
      sortable: true,
    },
    {
      name: 'Name',
      selector: (row: ItemData) => row.name,
      sortable: true,
    },
    {
      name: 'Category',
      selector: (row: ItemData) => row.category,
      sortable: true,
    },
    {
      name: 'IDDSI Level',
      selector: (row: ItemData) => row.iddsi_level, // Use iddsi_level
      sortable: true,
    },
    {
      name: 'Actions',
      cell: (row: ItemData) => (
        <>
          <button
            type="button"
            className="btn btn-primary mr-2"
            onClick={() => handleEdit(row)}
          >
            Edit
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={() => handleDelete(row.id)}
          >
            Delete
          </button>
        </>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-12">
          <h2 className="text-center">Items</h2>
          <div className="col-md-12 align-right">
            <button type="button" className="btn btn-primary m-3 w100" onClick={handleShow}>
              Add Item
            </button>

            <button type="button" className="btn btn-primary m-3 w100" onClick={handleShow}>
              Add Item bulk using CSV
            </button>
          </div>

          {/* Modal */}
          {showModal && (
            <>
              <div className="modal show d-block" role="dialog">
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">
                        {editMode ? 'Edit Item' : 'Add New Item'}
                      </h5>
                      <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                        <span aria-hidden="true">&times;</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-group mb-3">
                          <label htmlFor="name">Name</label>
                          <input
                            {...register('name', { required: true })}
                            type="text"
                            className="form-control"
                            id="name"
                            placeholder="Enter item name"
                          />
                          {errors.name && <span className="text-danger">This field is required</span>}
                        </div>

                        {/* Category Select Box */}
                        <div className="form-group mb-3">
                          <label htmlFor="category">Category</label>
                          <select
                            {...register('category', { required: true })}
                            className="form-control"
                            id="category"
                          >
                            <option value="">Select Category</option>
                            <option value="chicken">chicken</option>
                            <option value="pork">pork</option>
                            <option value="fish">fish</option>
                            <option value="veg">veg</option>
                            
                          </select>
                          {errors.category && <span className="text-danger">Category is required</span>}
                        </div>

                        {/* IDDSI Level Select Box */}
                        <div className="form-group mb-3">
                          <label htmlFor="iddsi_level">IDDSI Level</label>
                          <select
                            {...register('iddsi_level', { required: true })}
                            className="form-control"
                            id="iddsi_level"
                          >
                            <option value="">Select IDDSI Level</option>
                            <option value="1">Level 1</option>
                            <option value="2">Level 2</option>
                            <option value="3">Level 3</option>
                          </select>
                          {errors.iddsi_level && <span className="text-danger">IDDSI Level is required</span>}
                        </div>
                        <button type="submit" className="btn btn-primary w-100">
                          {editMode ? 'Update Item' : 'Add Item'}
                        </button>
                      </form>
                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" onClick={handleClose}>
                        Close
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-backdrop fade show"></div>
            </>
          )}

          {/* DataTable */}
          <DataTable
            title="Items List"
            columns={columns}
            data={itemsData} // Using dynamic data from state
            pagination
            selectableRows
            highlightOnHover
          />
        </div>
      </div>
    </div>
  );
};

export default ReadItems;
