import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import { useForm } from 'react-hook-form';
import ItemService from '../../services/ItemService'; // Adjust import as necessary
import Papa from 'papaparse';
import { ItemData, ItemsForm } from '../../types/ItemsForm'; // Import your types

const Items: React.FC = () => {
  const { register, handleSubmit, setValue, formState: { errors } } = useForm<ItemsForm>();
  const [itemsData, setItemsData] = useState<ItemData[]>([]);
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentItem, setCurrentItem] = useState<ItemData | null>(null);
  const [showCsvModal, setShowCsvModal] = useState(false);

  // Fetch data dynamically when the component mounts
  useEffect(() => {
    fetchItemsData();
  }, []);

  const fetchItemsData = async () => {
    try {
      const response = await ItemService.getItems(); // Adjust based on your service
      const items = response.data || response;
      const formattedItems = items.map((item: any) => ({
        id: item.id,
        name: item.name,
        category: item.category,
        iddsi_level: item.iddsi_level,
      }));
      setItemsData(formattedItems);
    } catch (error) {
      console.error('Error fetching items data:', error);
    }
  };

  // Handle form submission
  const onSubmit = async (data: ItemsForm) => {
    // Your logic for submitting data
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Items List</h2>
      <DataTable
        title="Items List"
        columns={[
         {
      name: 'ID',
      selector: (row: ItemData) => row.id,
      sortable: true,
    },
    {
      name: 'Name',
      selector: (row: ItemData) => row.name,
      sortable: true,
    },
    {
      name: 'Category',
      selector: (row: ItemData) => row.category,
      sortable: true,
    },
    {
      name: 'IDDSI Level',
      selector: (row: ItemData) => row.iddsi_level,
      sortable: true,
    },
    {
      name: 'Actions',
      cell: (row: ItemData) => (
        <>
          <button
            type="button"
            className="btn btn-primary mr-2"
            onClick={() => handleEdit(row)}
          >
            Edit
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={() => handleDelete(row.id)}
          >
            Delete
          </button>
        </>
      ),
      ignoreRowClick: true, // Keep this if you want to prevent row clicks from firing
    },
        ]}
        data={itemsData}
        pagination
        selectableRows
        highlightOnHover
      />
      {/* Add other JSX elements, modals, etc. */}
    </div>
  );
};

export default Items; // Ensure this export is present
