import React, { useState } from 'react';
import DataTable from 'react-data-table-component';
import { useForm } from 'react-hook-form';
import Commonservice from '../Commonservices';

interface ItemData {
  id: number;
  title: string;
  year: number;
}

interface ItemsForm {
  name: string;
  email: string;
  password: string;
  password_confirmation: string;
}

const ReadItems: React.FC = () => {
  const { register, handleSubmit, formState: { errors } } = useForm<ItemsForm>();

  const [showModal, setShowModal] = useState(false);

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  // Form submission logic
  const onSubmit = (data: ItemsForm) => {
    Commonservice.setItems(data.name, data.email, data.password, data.password_confirmation)
      .then(() => {
        alert('Registration successful!');
      })
      .catch(error => {
        console.error('Error during registration:', error);
      });
  };

  // Sample data
  const data: ItemData[] = [
    { id: 1, title: 'Item 1', year: 2020 },
    { id: 2, title: 'Item 2', year: 2021 },
    { id: 3, title: 'Item 3', year: 2019 },
    { id: 4, title: 'Item 4', year: 2022 },
  ];

  // Define columns for the DataTable
  const columns = [
    {
      name: 'ID',
      selector: (row: ItemData) => row.id,
      sortable: true,
    },
    {
      name: 'Title',
      selector: (row: ItemData) => row.title,
      sortable: true,
    },
    {
      name: 'Year',
      selector: (row: ItemData) => row.year,
      sortable: true,
    },
    {
      name: 'Action',
      cell: (row: ItemData) => (
        <button type="button" className="btn btn-primary" onClick={() => handleDelete(row.id)}>
          Delete
        </button>
         
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  // Delete item handler
  const handleDelete = (id: number) => {
    console.log(`Item with ID ${id} deleted.`);
    // You can add your delete logic here
  };

  const handleEdit = (id: number) => {
    console.log(`Item with ID ${id} deleted.`);
    // You can add your delete logic here
  };

  handleEdit

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-12">
          <h2 className="text-center">Items</h2>
          <div className="col-md-12 align-right">
            <button type="button" className="btn btn-primary m-3" onClick={handleShow}>
              Add Items
            </button>
            <button type="button" className="btn btn-primary m-3" onClick={handleShow}>
              Add Bulk Items
            </button>
          </div>

          {/* Modal */}
          {showModal && (
            <>
              <div className="modal show d-block" role="dialog">
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">Add New Item</h5>
                      <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                        <span aria-hidden="true">&times;</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-group mb-3">
                          <label htmlFor="name">Name</label>
                          <input
                            {...register('name', { required: true })}
                            type="text"
                            className="form-control"
                            id="name"
                            placeholder="Enter your name"
                          />
                          {errors.name && <span className="text-danger">This field is required</span>}
                        </div>

                        <div className="form-group mb-3">
                          <label htmlFor="email">Email</label>
                          <input
                            {...register('email', { required: true })}
                            type="email"
                            className="form-control"
                            id="email"
                            placeholder="Enter your email"
                          />
                          {errors.email && <span className="text-danger">This field is required</span>}
                        </div>

                        <div className="form-group mb-3">
                          <label htmlFor="password">Password</label>
                          <input
                            {...register('password', { required: true })}
                            type="password"
                            className="form-control"
                            id="password"
                            placeholder="Enter your password"
                          />
                          {errors.password && <span className="text-danger">This field is required</span>}
                        </div>

                        <div className="form-group mb-3">
                          <label htmlFor="password_confirmation">Confirm Password</label>
                          <input
                            {...register('password_confirmation', { required: true })}
                            type="password"
                            className="form-control"
                            id="password_confirmation"
                            placeholder="Confirm your password"
                          />
                          {errors.password_confirmation && <span className="text-danger">This field is required</span>}
                        </div>

                        <button type="submit" className="btn btn-primary w-100">
                          Register
                        </button>
                      </form>
                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" onClick={handleClose}>
                        Close
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-backdrop fade show"></div>
            </>
          )}

          {/* DataTable */}
          <DataTable
            title="Items List"
            columns={columns}
            data={data}
            pagination
            selectableRows
            highlightOnHover
          />
        </div>
      </div>
    </div>
  );
};

export default ReadItems;
