import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import Papa from "papaparse";
import { ItemData, ItemsForm } from "../../../types/ItemsForm"; // Import your types
import FoodItemlist from "./components/FoodItemlist";
import useGetFoodItemService from "./hooks/useGetFoodItemService";
import { FoodItemToolBar } from "./components/FoodItemToolBar";
import { AddEditFoodItemForm } from "./components/AddEditFoodItemForm";
import { ImportFoodItemForm } from "./components/ImportFoodItemForm";

const FoodItems: React.FC = () => {
  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
    reset,
  } = useForm<ItemsForm>();
  const [itemsData, setItemsData] = useState<ItemData[]>([]);
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentItem, setCurrentItem] = useState<ItemData | null>(null);
  const [showCsvModal, setShowCsvModal] = useState(false);
  const [loading, setLoading] = useState(false);

  const { getItems, setItems, updateItems, deleteItems } =
    useGetFoodItemService();

  // Fetch data dynamically when the component mounts
  useEffect(() => {
    fetchItemsData();
  }, []);

  // Fetch items data from the backend
  const fetchItemsData = async () => {
    setLoading(true); // Show loading state
    try {
      const response = await getItems(); // Adjust based on your service
      const items = response.data || response;
      const formattedItems = items.map((item: any) => ({
        id: item.id,
        name: item.name,
        category: item.category,
        iddsi_level: item.iddsi_level,
      }));
      setItemsData(formattedItems);
    } catch (error) {
      console.error("Error fetching items data:", error);
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  // Handle opening the form modal
  const handleShow = () => {
    setShowModal(true);
    setEditMode(false);
    reset(); // Clear form fields when adding a new item
  };

  // Handle closing the modals (both CSV and form modals)
  const handleClose = () => {
    setShowModal(false);
    setShowCsvModal(false);
    setCurrentItem(null);
  };

  // Handle item edit
  const handleEdit = (item: ItemData) => {
    setEditMode(true);
    setCurrentItem(item);
    setShowModal(true);
    setValue("name", item.name);
    setValue("category", item.category);
    setValue("iddsi_level", item.iddsi_level);
  };

  // Handle delete item
  const handleDelete = async (id: number) => {
    try {
      await deleteItems(id);
      setItemsData((prevItems) => prevItems.filter((item) => item.id !== id)); // Update state
      alert(`Item with ID ${id} deleted successfully.`);
    } catch (error) {
      console.error(`Error deleting item with ID ${id}:`, error);
      alert(`Error deleting item with ID ${id}`);
    }
  };

  // Handle form submission
  const onSubmit = async (data: ItemsForm) => {
    try {
      if (editMode && currentItem) {
        // Edit existing item
        await updateItems(
          currentItem.id,
          data.name,
          data.category,
          data.iddsi_level
        );
        setItemsData((prevItems) =>
          prevItems.map((item) =>
            item.id === currentItem.id
              ? {
                  ...item,
                  name: data.name,
                  category: data.category,
                  iddsi_level: data.iddsi_level,
                }
              : item
          )
        );
        alert("Item updated successfully!");
      } else {
        // Add new item
        const response = await setItems(
          data.name,
          data.category,
          data.iddsi_level
        );
        setItemsData((prevItems) => [...prevItems, response.data]);
        alert("New item added successfully!");
      }
      handleClose(); // Close modal after success
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Error saving item.");
    }
  };

  // Handle CSV upload
  const handleCsvUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      Papa.parse(file, {
        complete: async (results: any) => {
          const data = results.data.slice(1); // Exclude header row
          const parsedItems: ItemData[] = data.map(
            (row: any, index: number) => ({
              id: itemsData.length + index + 1,
              name: row[0],
              category: row[1],
              iddsi_level: row[2],
            })
          );

          try {
            for (const item of parsedItems) {
              await setItems(item.name, item.category, item.iddsi_level);
            }
            fetchItemsData(); // Refresh item list after upload
            alert("Items uploaded and saved successfully!");
          } catch (error) {
            console.error("Error uploading CSV:", error);
            alert("Error uploading CSV.");
          }
          handleClose(); // Close modal after upload
        },
        header: false,
      });
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-12">
          <h2 className="text-center">Items</h2>

          <FoodItemToolBar
            handleShow={handleShow}
            setShowCsvModal={setShowCsvModal}
          />

          {/* Modal for Add/Edit Item */}
          {showModal && (
            <AddEditFoodItemForm
              editMode={editMode}
              handleClose={handleClose}
              onSubmit={onSubmit}
            />
          )}

          {/* CSV Upload Modal */}
          {showCsvModal && (
            <ImportFoodItemForm
              handleClose={handleClose}
              handleCsvUpload={handleCsvUpload}
            />
          )}

          {/* Display the list of items */}
          <FoodItemlist itemsData={itemsData as ItemData[]} />

          {/* Show loading state if data is being fetched */}
          {loading && <div>Loading items...</div>}
        </div>
      </div>
    </div>
  );
};

export default FoodItems;
