import React from "react";
import Papa from "papaparse";
import { ItemData } from "../../../../types/ItemsForm";
import useGetFoodItemService from "../hooks/useGetFoodItemService";

interface ImportFoodItemFormProps {
  handleClose: () => void;
  fetchItemsData: () => void;
  itemsData: ItemData[];
}

export const ImportFoodItemForm: React.FC<ImportFoodItemFormProps> = ({
  handleClose,
  fetchItemsData,
  itemsData,
}) => {
  const { setItems } = useGetFoodItemService();

  // Handle CSV upload
  const handleCsvUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      Papa.parse(file, {
        complete: async (results: any) => {
          console.log("Parsed results:", results); // Log parsed results
          const data = results.data.slice(1); // Exclude header row

          // Validate the parsed data
          const parsedItems: ItemData[] = data.map((row: any) => {
            if (row.length < 3) {
              console.error("Invalid row:", row); // Log invalid rows
              return null; // Return null for invalid rows
            }
            return {
              id: itemsData.length + parsedItems.length + 1, // Unique ID
              name: row[0],
              category: row[1],
              iddsi_level: row[2],
            };
          }).filter(Boolean) as ItemData[]; // Filter out invalid rows

          if (parsedItems.length === 0) {
            alert("No valid items to upload."); // No valid items found
            return;
          }

          try {
            for (const item of parsedItems) {
              await setItems(item.name, item.category, item.iddsi_level);
            }
            fetchItemsData(); // Refresh item list after upload
            alert("Items uploaded and saved successfully!");
          } catch (error) {
            console.error("Error during item upload:", error); // Log specific errors
            alert("Error uploading items to server.");
          }

          handleClose(); // Close modal after upload
        },
        header: false,
      });
    }
  };

  return (
    <>
      <div className="modal show d-block" role="dialog">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Upload CSV</h5>
              <button type="button" className="close" onClick={handleClose}>
                <span>&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <input
                type="file"
                className="form-control mb-3"
                accept=".csv"
                onChange={handleCsvUpload}
              />
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleClose}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="modal-backdrop fade show"></div>
    </>
  );
};
