// src/services/ItemService.ts
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

const ItemService = {
  getItems: () => {
    return axios.get(`${API_URL}/items`);
  },

  setItems: (name: string, category: string, iddsi_level: string) => {
    return axios.post(`${API_URL}/items`, { name, category, iddsi_level });
  },

  updateItems: (id: number, name: string, category: string, iddsi_level: string) => {
    return axios.put(`${API_URL}/items/${id}`, { name, category, iddsi_level });
  },

  deleteItems: (id: number) => {
    return axios.delete(`${API_URL}/items/${id}`);
  },
};

export default ItemService;
