import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import { useForm } from 'react-hook-form';
import ItemService from '../../../services/ItemService'; // Adjust import as necessary
import Papa from 'papaparse';
import { ItemData, ItemsForm } from '../../../types/ItemsForm'; // Import your types

const FoodItems: React.FC = () => {
  const { register, handleSubmit, setValue, formState: { errors } } = useForm<ItemsForm>();
  const [itemsData, setItemsData] = useState<ItemData[]>([]);
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentItem, setCurrentItem] = useState<ItemData | null>(null);
  const [showCsvModal, setShowCsvModal] = useState(false);

  // Fetch data dynamically when the component mounts
  useEffect(() => {
    fetchItemsData();
  }, []);

   // Edit item handler
  const handleEdit = (item: ItemData) => {
    setEditMode(true);
    setCurrentItem(item);
    setShowModal(true);
    setValue('id', item.id);
    setValue('name', item.name);
    setValue('category', item.category);
    setValue('iddsi_level', item.iddsi_level);
  };

  const handleShow = () => {
    setShowModal(true);
    setEditMode(false);
  };

  const handleClose = () => {
    setShowModal(false);
    setCurrentItem(null);
  };

  // CSV Upload Handler
  const handleCsvUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      Papa.parse(file, {
        complete: async function (results: any) {
          const data = results.data;
          const parsedItems: ItemData[] = data.slice(1).map((row: any, index: number) => ({
            id: itemsData.length + index + 1,
            name: row[0],
            category: row[1],
            iddsi_level: row[2],
          }));

          // Upload each parsed item to the backend
          for (const item of parsedItems) {
            try {
              await ItemService.setItems(item.name, item.category, item.iddsi_level);
            } catch (error) {
              console.error('Error uploading item:', item, error);
            }
          }

          fetchItemsData();
          alert("Items uploaded and saved successfully!");
        },
        header: false,
      });
    }
    handleClose();
  };

  const handleShowCsvModal = () => {
    setShowCsvModal(true);
  };

  // Delete item handler
  const handleDelete = async (id: number) => {
    try {
      await ItemService.deleteItems(id); // Call to delete the item from the backend
      const filteredItems = itemsData.filter(item => item.id !== id);
      setItemsData(filteredItems); // Update the state by removing the deleted item
      alert(`Item with ID ${id} deleted successfully.`);
    } catch (error) {
      console.error(`Error deleting item with ID ${id}:`, error);
      alert(`Error deleting item with ID ${id}`);
    }
  };

  const fetchItemsData = async () => {
    try {
      const response = await ItemService.getItems(); // Adjust based on your service
      const items = response.data || response;
      const formattedItems = items.map((item: any) => ({
        id: item.id,
        name: item.name,
        category: item.category,
        iddsi_level: item.iddsi_level,
      }));
      setItemsData(formattedItems);
    } catch (error) {
      console.error('Error fetching items data:', error);
    }
  };

  // Handle form submission
  const onSubmit = async (data: ItemsForm) => {
