import ReactDOM from 'react-dom';
import React, { useEffect, useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import DataTable from "react-data-table-component";

export default function ReadItems() {

// Define the structure of your data
interface ItemData {
  id: number;
  title: string;
  year: number;
}

// Sample data
const data: ItemData[] = [
  { id: 1, title: 'Item 1', year: 2020 },
  { id: 2, title: 'Item 2', year: 2021 },
  { id: 3, title: 'Item 3', year: 2019 },
  { id: 4, title: 'Item 4', year: 2022 },
];

 const [showModal, setShowModal] = useState(false);

  // Function to show modal
  const handleShow = () => setShowModal(true);

  // Function to hide modal
  const handleClose = () => setShowModal(false);

// Define columns for the DataTable
const columns = [
  {
    name: 'ID',
    selector: (row: ItemData) => row.id, // Type explicitly set for 'row'
    sortable: true,
  },
  {
    name: 'Title',
    selector: (row: ItemData) => row.title, // Type explicitly set for 'row'
    sortable: true,
  },
  {
    name: 'Year',
    selector: (row: ItemData) => row.year, // Type explicitly set for 'row'
    sortable: true,
  },
];


    return (

    <div className="container mt-5">
    <div className="row justify-content-center">
    <div className="col-md-12">
    <h2 className="text-center">Items</h2>
    <div className="col-md-12">
       <div>
      {/* Button to Open Modal */}
      <button type="button" className="btn btn-primary p-3" onClick={handleShow}>
        Add Items
      </button>

      <button type="button" className="btn btn-primary" onClick={handleShow}>
        Add bulk Items
      </button>
      {showModal && (
        <div className="modal show d-block" role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Modal title</h5>
                <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>This is the content inside the modal.</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleClose}>
                  Close
                </button>
                <button type="button" className="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      {showModal && <div className="modal-backdrop fade show"></div>}
    </div>
    </div>
     <DataTable
        title="Items List"
        columns={columns}
        data={data}
        pagination
        selectableRows
        highlightOnHover
      />    
    </div>
    </div>
    </div>
    )
}
