import React, { useState } from 'react';
import DataTable from 'react-data-table-component';
import { useForm } from 'react-hook-form';
import Commonservice from '../Commonservices';

interface ItemData {
  id: number;
  title: string;
  year: number;
}

interface ItemsForm {
  name: string;
  email: string;
  password: string;
  password_confirmation: string;
}

const ReadItems: React.FC = () => {
  const { items , handleSubmit, setValue, formState: { errors } } = useForm<ItemsForm>();
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false); // To handle edit mode
  const [currentItem, setCurrentItem] = useState<ItemData | null>(null); // To store the item being edited

  const handleShow = () => {
    setShowModal(true);
    setEditMode(false); // Reset edit mode on new item creation
  };

  const handleClose = () => {
    setShowModal(false);
    setCurrentItem(null); // Reset current item
  };

  // Form submission logic
  const onSubmit = (data: ItemsForm) => {
    if (editMode && currentItem) {
      // Logic for updating the item
      console.log('Updating item:', currentItem.id, data);
      alert('Item updated successfully!');
    } else {
      // Logic for creating a new item
      Commonservice.setItems(data.name, data.email, data.password, data.password_confirmation)
        .then(() => {
          alert('Registration successful!');
        })
        .catch(error => {
          console.error('Error during registration:', error);
        });
    }
    handleClose(); // Close modal after submit
  };

  // Sample data
  const data: ItemData[] = [
    { id: 1, title: 'Item 1', year: 2020 },
    { id: 2, title: 'Item 2', year: 2021 },
    { id: 3, title: 'Item 3', year: 2019 },
    { id: 4, title: 'Item 4', year: 2022 },
  ];

  // Define columns for the DataTable
  const columns = [
    {
      name: 'ID',
      selector: (row: ItemData) => row.id,
      sortable: true,
    },
    {
      name: 'Title',
      selector: (row: ItemData) => row.title,
      sortable: true,
    },
    {
      name: 'Year',
      selector: (row: ItemData) => row.year,
      sortable: true,
    },
    {
      name: 'Actions',
      cell: (row: ItemData) => (
        <>
          <button
            type="button"
            className="btn btn-primary mr-2"
            onClick={() => handleEdit(row)}
          >
            Edit
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={() => handleDelete(row.id)}
          >
            Delete
          </button>
        </>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  // Edit item handler
  const handleEdit = (item: ItemData) => {
    setEditMode(true);
    setCurrentItem(item);
    setShowModal(true);
    // Prefill the form with the current item's data
    setValue('name', item.title); // Assuming `name` is the title of the item
    setValue('email', ''); // Assuming you want to reset or modify the email
    setValue('password', ''); // Resetting password
    setValue('password_confirmation', ''); // Resetting password confirmation
  };

  // Delete item handler
  const handleDelete = (id: number) => {
    console.log(`Item with ID ${id} deleted.`);
    // You can add your delete logic here
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-12">
          <h2 className="text-center">Items</h2>
          <div className="col-md-12 align-right">
            <button type="button" className="btn btn-primary m-3 w100" onClick={handleShow}>
              Add Items
            </button>
            <button type="button" className="btn btn-primary m-3 w100" onClick={handleShow}>
              Add Bulk Items
            </button>
          </div>

          {/* Modal */}
          {showModal && (
            <>
              <div className="modal show d-block" role="dialog">
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title">
                        {editMode ? 'Edit Item' : 'Add New Item'}
                      </h5>
                      <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                        <span aria-hidden="true">&times;</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-group mb-3">
                          <label htmlFor="name">Name</label>
                          <input
                            {...register('name', { required: true })}
                            type="text"
                            className="form-control"
                            id="name"
                            placeholder="Enter your name"
                          />
                          {errors.name && <span className="text-danger">This field is required</span>}
                        </div>
                        <button type="submit" className="btn btn-primary w-100">
                          {editMode ? 'Update Item' : 'Register'}
                        </button>
                      </form>
                    </div>
                    <div className="modal-footer">
                      <button type="button" className="btn btn-secondary" onClick={handleClose}>
                        Close
                      </button>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-backdrop fade show"></div>
            </>
          )}

          {/* DataTable */}
          <DataTable
            title="Items List"
            columns={columns}
            data={data}
            pagination
            selectableRows
            highlightOnHover
          />
        </div>
      </div>
    </div>
  );
};

export default ReadItems;
